    classify: function(thesis, bClassifierThesis, type) {

        if (thesis == null) {
            return "Theses is null";
        }

        //TODO Test more criteria
        switch(type) {
            case 0:
                return bClassifierThesis.classify(thesis.title);
                break;
            case 1:
                return bClassifierThesis.classify(thesis.title + thesis.objectives);
                break;
            case 2:
                return bClassifierThesis.classify(thesis.title + thesis.requirements);
                break;
            case 3:
                return bClassifierThesis.classify(thesis.title + thesis.objectives + thesis.requirements);
                break;
            case 4:
                return bClassifierThesis.classify(thesis.title + thesis.location);
                break;
            case 5:
                let professors = "";
              console.log(thesis.supervisors);
                for (prof of thesis.supervisors)    {
                    startIndex = prof.indexOf("(");
                    endIndex = prof.indexOf(")");
                    replacement = "";
                    toBeReplaced = prof.substring(startIndex, endIndex + 1);
                    professors += prof.replace(toBeReplaced, replacement);
                }

                return bClassifierThesis.classify(thesis.title + professors);
            break;
            default:
                return bClassifierThesis.classify(thesis.title);
        }





    },

